import React, { Component } from "react";
import item1 from "../img/item1.jpg";
import { connect } from "react-redux";
import { cartFetch, cartUpdate } from "../Actions/userAction";
import axios from "axios";
class Cart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sum: 0
    };
  }
  handle_plus = (id, item_total, index) => {
    console.log("id", id);
    console.log("item", item_total);
    console.log("index", index);
    axios
      .put(
        "http://localhost:3001/user/updatecartbyid/" + id,
        { item_total: "2" },
        {
          headers: {
            auth_token: localStorage.getItem("auth_token")
          }
        }
      )
      .then(data => {
        this.props.cartUpdate(data.data);
      })
      .catch(err => {
        console.log(err);
      });
  };

  btn_delete = id => {
    axios
      .delete("http://localhost:3001/user/deletecartitembyid/" + id, {
        headers: {
          auth_token: localStorage.getItem("auth_token")
        }
      })
      .then(data => {
        if (data.data.deletedCount > 0) {
          axios
            .get("http://localhost:3001/user/getcartitembyid", {
              headers: {
                auth_token: localStorage.getItem("auth_token")
              }
            })
            .then(data => {
              if (data) {
                console.log(data.data);
                this.props.cartFetch(data.data);
              }
            })
            .catch(err => {
              if (err) console.log(err.response.data);
            });
        }
      });
  };
  componentDidMount() {
    axios
      .get("http://localhost:3001/user/getcartitembyid", {
        headers: {
          auth_token: localStorage.getItem("auth_token")
        }
      })
      .then(data => {
        if (data) {
          console.log(data.data);
          this.props.cartFetch(data.data);
        }
      })
      .catch(err => {
        console.log(err.response.data);
      });
  }
  render() {
    console.log("props", this.props.cart);
    return (
      <div>
        <div className="conatiner-fluid bg-light">
          <p className="">
            <span className="" style={{ fontSize: "35px" }}>
              My Food Bag
            </span>
          </p>
        </div>
        <div className="container">
          <div className="row mt-3">
            <div className="col-md-9 ">
              <table class="table border">
                <thead>
                  <tr className="">
                    <th scope="col">No.</th>
                    <th scope="col">Item-Description</th>
                    <th scope="col">Qty</th>
                    <th scope="col">Price (Rs.)</th>
                    <th scope="col">Remove</th>
                  </tr>
                </thead>
                <tbody>
                  {this.props.cart ? (
                    this.props.cart.map((value, index) => {
                      return (
                        <tr>
                          <th scope="row">{index + 1}</th>
                          <td>
                            <div className="row">
                              <div className="col-md-3">
                                <img src={item1} className="w-100"></img>
                              </div>
                              <div className="col-md-9">
                                <p>
                                  {value.item_name} {value.item_qty}
                                </p>
                              </div>
                            </div>
                          </td>
                          <td className="text-center">
                            <div className="row">
                              <div className="col-md-2">
                                <button className="btn btn-sm btn-outline-success">
                                  -
                                </button>
                              </div>
                              <div className="col-md-2 ml-2">
                                <span className="h5">{value.item_total}</span>
                              </div>
                              <div className="col-md-2">
                                <button
                                  className="btn btn-sm btn-outline-success"
                                  onClick={() => {
                                    this.handle_plus(
                                      value._id,
                                      value.item_total,
                                      index
                                    );
                                  }}
                                >
                                  +
                                </button>
                              </div>
                              <div className="col-md-6"></div>
                            </div>
                          </td>
                          <td>{value.item_price}/-</td>
                          <td className="text-center">
                            <button
                              className="btn btn-sm btn-outline-danger ml-2"
                              onClick={() => {
                                this.btn_delete(value._id);
                              }}
                            >
                              <span className="h5">
                                <i class="far fa-trash-alt"></i>
                              </span>
                            </button>
                          </td>
                        </tr>
                      );
                    })
                  ) : (
                    <div>Cart is Empty</div>
                  )}
                </tbody>
              </table>
            </div>
            <div className="col-md-3 bg-light ">
              <p className="h5">Summary</p>
              <p>
                <span style={{ fontSize: "14px" }}>
                  Estimate Shipping and Tax
                </span>
              </p>
              <p>
                <button
                  class="btn btn-outline-primary"
                  type="button"
                  data-toggle="collapse"
                  data-target="#collapseExample"
                  aria-expanded="false"
                  aria-controls="collapseExample"
                >
                  Apply Discount Code
                </button>
              </p>
              <div class="collapse" id="collapseExample">
                <div class="card card-body">
                  <div className="row">
                    <div className="col-md-7">
                      <h4>
                        <span class="badge badge-secondary">New</span>
                      </h4>
                    </div>
                    <div className="col-md-5">
                      <button type="submit" class="btn btn-default btn-sm">
                        Apply
                      </button>
                    </div>
                  </div>
                </div>
              </div>
              <p>
                <div className="row">
                  <div className="col-md-6">
                    <span style={{ fontSize: "14px" }}>Subtotal</span>
                  </div>
                  <div className="col-md-6 text-right text-success">
                    <span className="" style={{ fontSize: "16px" }}>
                      {this.props.cart
                        ? "40" // this.props.cart.map((value, index) => {
                        : //   return this.setState({sum:this.state.sum+parseInt(value.item_price)})
                          //   })
                          ""}
                      Rs.
                    </span>
                  </div>
                </div>
              </p>
              <p>
                <div className="row">
                  <div className="col-md-6">
                    <span style={{ fontSize: "14px" }}>Shipping</span>
                  </div>
                  <div className="col-md-6 text-right text-success">
                    <span className="" style={{ fontSize: "16px" }}>
                      20.00 Rs.
                    </span>
                  </div>
                </div>
              </p>
              <p>
                <div className="row">
                  <div className="col-md-6 ">
                    <span style={{ fontSize: "14px" }}>Coupon Discount</span>
                  </div>
                  <div className="col-md-6 text-right text-danger">
                    <span className="" style={{ fontSize: "16px" }}>
                      -20.00 Rs.
                    </span>
                  </div>
                </div>
              </p>
              <p>
                <hr></hr>
              </p>
              <p>
                <div className="row">
                  <div className="col-md-6">
                    <span style={{ fontSize: "14px" }}>Order Total</span>
                  </div>
                  <div className="col-md-6 text-right text-success">
                    <span
                      className="font-weight-bold"
                      style={{ fontSize: "20px" }}
                    >
                      40.00 Rs.
                    </span>
                  </div>
                </div>
              </p>

              <p>
                <div className="row">
                  <div className="col-md-12">
                    <button className="btn btn-green1 text-light form-control">
                      Checkout
                    </button>
                  </div>
                </div>
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  const { isLoggedIn, cart } = state;
  return { isLoggedIn, cart };
};
const mapDispatchToProps = dispatch => {
  return {
    cartFetch: cartitems => dispatch(cartFetch(cartitems)),
    cartUpdate: cart => dispatch(cartUpdate(cart))
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Cart);
