import React, { Component } from "react";
import { connect } from "react-redux";
import axios from "axios";
import { Link } from "react-router-dom";
import item1 from "../img/item1.jpg";
class DisplayItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      providerkitchen: "",
      foods: "",
      error: ""
    };
  }
  btn_add = value => {
    console.log("btnadd", value);
    console.log();
    axios
      .post("http://localhost:3001/user/insertusercart", {...value,food_total:'1'}, {
        headers: {
          auth_token: localStorage.getItem("auth_token")
        }
      })
      .then(data => {
        if (data) alert('Item added!');
      })
      .catch(err => {
        if (err) alert("" + err.response.data);
        this.setState({ error: err.response.data });
      });
  };
  componentDidMount() {
    axios
      .get(
        "http://localhost:3001/user/getuniquekitchen/" +
          this.props.match.params.id,
        {
          headers: {
            auth_token: localStorage.getItem("auth_token")
          }
        }
      )
      .then(data => {
        console.log(data.data);
        this.setState({ providerkitchen: data.data });
      })
      .catch(err => {
        if (err) console.log(err);
      });

    axios
      .get(
        "http://localhost:3001/user/getfooditems/" + this.props.match.params.id,
        {
          headers: {
            auth_token: localStorage.getItem("auth_token")
          }
        }
