const initialState = {
  isLoggedIn: false,
  isRegistered: false,
  fullname: "",
  email: "",
  id: "",
  test: "vandan"
};
const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case "USER_LOGIN":
      return {
        ...state,
        isLoggedIn: true,
        fullname: action.payload.fullname,
        email: action.payload.email,
        id: action.payload.id
      };
    case "USER_REGISTER":
      return {
        ...state,
        isRegistered: true
      };
    case "USER_LOGOUT":
      return {
        ...state,
        isLoggedIn: false
      };
      case "FETCH_KITCHEN":
      return {
        ...state,
        isLoggedIn: false
      };
    case "USER_TEST":
      return {
        ...state,
        test: state.state.test + "Khamkar"
      };
    default:
      return state;
  }
};
export default userReducer;
