import React from "react";
import Badge from '@material-ui/core/Badge';
import { withStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import { Link, Redirect } from "react-router-dom";
import "../style.css";
import {
  userLogin,
  userRegister,
  userTest,
  userLogout
} from "../Actions/userAction";
//import { useSelector } from "react-redux";
import { connect } from "react-redux";
import axios from "axios";
class Nav extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    isLoggedIn: false,
    isRegistered: false,
    fullname: "",
    email: "",
    password: "",
    error: "",
    data: "",
    redirect: false
  };
  handle_Register = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  handle_Providerlogin = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  handle_Adminlogin = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  handle_Submituser = e => {
    e.preventDefault();
    e.target.reset();
    axios
      .post("http://localhost:3001/user/signup", {
        fullname: this.state.fullname,
        email: this.state.email,
        password: this.state.password
      })
      .then(data => {
        if (data) {
          console.log(data.data);
          this.setState({ isRegistered: true, error: "" });
          this.props.userRegister();
        }
      })
      .catch(err => {
        if (err) {
          this.setState({ error: err.response.data, isRegistered: false });
        }
      });
  };
  handle_Submit = e => {
    e.preventDefault();
  };
  handle_Logout = () => {
    this.props.userLogout();
    localStorage.clear();
  };
  componentWillMount() {
    if (localStorage.getItem("auth_token")) {
      axios
        .get("http://localhost:3001/user/info", {
          headers: {
            "Content-Type": "application/json",
            auth_token: localStorage.getItem("auth_token")
          }
        })
        .then(data => {
          if (data) {
            console.log(data.data.fullname);
            const userdata = {
              fullname: data.data.fullname,
              email: data.data.email.email,
              id: ""
            };
            this.props.userLogin(userdata);
          }
        });
    }
  }
  render() {
    return (
      <React.Fragment>
        {this.props.isLoggedIn ? (
          <Redirect to="/userdashboard"></Redirect>
        ) : (
          <Redirect to="/"></Redirect>
        )}
        <div>
          <nav className="navbar navbar-expand-lg ">
            <a className="navbar-brand text-dark" href="#">
              <h4>{/* TiffinService<small>.com </small> */}</h4>
            </a>
            <button
              className="navbar-toggler"
              type="button"
              style={{ backgroundColor: "white" }}
              data-toggle="collapse"
              data-target="#navbarSupportedContent"
              aria-controls="navbarSupportedContent"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span
                className="navbar-toggler-icon"
                style={{ backgroundColor: "#21b725" }}
              ></span>
            </button>

            <div
              className="collapse navbar-collapse"
              id="navbarSupportedContent"
            >
              <ul className="navbar-nav mr-auto nav-color">
                <li className="nav-item active ">
                  <a className="nav-link a-link" href="#">
                    Home <span className="sr-only">(current)</span>
                  </a>
                </li>
                {this.props.isLoggedIn ? (
                  <li className="nav-item">
                    <Link
                      to="/userdashboard"
                      className="nav-link a-link"
                      href="#"
                    >
                      My Dashboard
                    </Link>
                  </li>
                ) : (
                  <li className="nav-item">
                    <a className="nav-link a-link" href="#">
                      Link
                    </a>
                  </li>
                )}
                <li className="nav-item">
                  <a className="nav-link a-link" href="#">
                    Apply for Business
                  </a>
                  {this.state.emj}
                </li>
              </ul>
              {this.props.isLoggedIn ? (
                <React.Fragment>
                  <li className="nav-item active mr-1">
                      <Link to="/cart" className="nav-link a-link" href="#">
                        <IconButton aria-label="cart">
                          <StyledBadge badgeContent={4} color="secondary">
                            <ShoppingCartIcon />
                          </StyledBadge>
                        </IconButton>
                      </Link>{" "}
                    </li>
                  <ul className="navbar-nav nav-color">
                    
                    <li className="nav-item ">
                      <div className="dropdown">
                        <button
                          className="btn btn-light dropdown-toggle"
                          type="button"
                          id="dropdownMenuButton"
                          data-toggle="dropdown"
                          aria-haspopup="true"
                          aria-expanded="false"
                        >
                          {this.props.fullname}
                        </button>
                        <div
                          className="dropdown-menu"
                          style={{ left: "auto", right: "0" }}
                          aria-labelledby="dropdownMenuButton"
                        >
                          <a className="dropdown-item" href="#">
                            {" "}
                            Notification
                          </a>
                          <a className="dropdown-item" href="#">
                            My Profile
                          </a>
                          <a className="dropdown-item" href="#">
                            Setting
                          </a>
                          <a className="dropdown-item" href="#">
                            Help
                          </a>
                          <a
                            className="dropdown-item text-light bg-danger"
                            href=""
                            onClick={this.handle_Logout}
                          >
                            Log out
                          </a>
                        </div>
                      </div>
                    </li>
                  </ul>
                </React.Fragment>
              ) : (
                <React.Fragment>
                  <button
                    className="btn btn-outline-light my-2 my-sm-0"
                    type="submit"
                    data-toggle="modal"
                    data-target="#mySignUpModal"
                  >
                    Sign Up
                  </button>
                  <button
                    className="btn btn-outline-light ml-2 my-2 my-sm-0"
                    type="submit"
                    data-toggle="modal"
                    data-target="#myModal"
                  >
                    Login
                  </button>
                </React.Fragment>
              )}
            </div>
            <div className="modal" id="myModal">
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-header">
                    <h4 className="modal-title"> Account Login</h4>
                    <button
                      type="button"
                      className="close"
                      data-dismiss="modal"
                    >
                      &times;
                    </button>
                  </div>

                  <div className="modal-body">
                    <ul
                      className="nav nav-tabs md-tabs tabs-3 light-blue "
                      role="tablist"
                    >
                      <li className="nav-item waves-effect waves-light">
                        <a
                          className="nav-link text-dark  active"
                          id="user-button-bg"
                          style={{ width: "230px" }}
                          data-toggle="tab"
                          href="#panel1"
                          role="tab"
                          aria-selected="true"
                        >
                          <i className="fas fa-user mr-1"></i> Provider
                        </a>
                      </li>

                      <li className="nav-item waves-effect waves-light ">
                        <a
                          className="nav-link text-light btn-green1 "
                          style={{ width: "230px" }}
                          id="admin-button-bg"
                          data-toggle="tab"
                          href="#panel2"
                          role="tab"
                          aria-selected="false"
                        >
                          <i className="fas fa-user-plus mr-1"></i> Admin
                        </a>
                      </li>
                    </ul>

                    <div className="tab-content">
                      <div
                        className="tab-pane fade in active show"
                        id="panel1"
                        role="tabpanel"
                      >
                        <form onSubmit={this.handle_Submit}>
                          <div className="form-group">
                            <input
                              type="email"
                              className="form-control input-width mt-3"
                              placeholder="Your Email"
                              name="email"
                              onChange={this.handle_Providerlogin}
                              required
                            />
                          </div>
                          <div className="form-group">
                            <input
                              type="password"
                              className="form-control input-width"
                              placeholder="Your Password"
                              name="password"
                              onChange={this.handle_Providerlogin}
                              required
                            />
                          </div>
                          <div className="form-group">
                            <button className="form-control btn bg-green1 btn-outline input-width text-light">
                              Login
                            </button>
                          </div>
                          <p className="text-center">
                            <Link to="/">Forgot password? |</Link>
                            <Link to="/"> Contact us</Link>
                          </p>
                        </form>
                      </div>

                      <div
                        className="tab-pane fade"
                        id="panel2"
                        role="tabpanel"
                      >
                        <div className="modal-body">
                          <form onSubmit={this.handle_Submit}>
                            <div className="form-group">
                              <input
                                type="email"
                                className="form-control input-width"
                                placeholder="Your Email"
                                name="email"
                                onChange={this.handle_Adminlogin}
                                required
                              />
                            </div>
                            <div className="form-group">
                              <input
                                type="password"
                                className="form-control input-width"
                                placeholder="Your Password"
                                name="password"
                                onChange={this.handle_Adminlogin}
                                required
                              />
                            </div>
                            <div className="form-group">
                              <button className="form-control btn bg-green1 btn-outline-light input-width">
                                Login
                              </button>
                            </div>
                            <p className="text-center">
                              <Link to="/">Forgot password? |</Link>
                              <Link to="/"> Contact us</Link>
                            </p>
                          </form>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="modal modal-signup" id="mySignUpModal">
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-header">
                    <h4 className="modal-title"> Create Personal Account</h4>
                    <button
                      type="button"
                      className="close"
                      data-dismiss="modal"
                    >
                      &times;
                    </button>
                  </div>

                  <div className="modal-body">
                    <div className="tab-content">
                      <div
                        className="tab-pane fade in active show"
                        id="panel1"
                        role="tabpanel"
                      >
                        <form onSubmit={this.handle_Submituser}>
                          <p className="text-light bg-green1 text-left h4">
                            <span> User Registration</span>
                          </p>
                          <div className="form-group">
                            <input
                              type="text"
                              className="form-control input-width mt-3"
                              placeholder="Your Fullname"
                              name="fullname"
                              onChange={this.handle_Register}
                              required
                            />
                          </div>
                          <div className="form-group">
                            <input
                              type="email"
                              className="form-control input-width mt-3"
                              placeholder="Your Email"
                              name="email"
                              onChange={this.handle_Register}
                              required
                            />
                          </div>

                          <div className="form-group">
                            <input
                              type="password"
                              className="form-control input-width"
                              placeholder="Create password"
                              name="password"
                              onChange={this.handle_Register}
                              required
                            />
                          </div>

                          <div className="form-group">
                            <button
                              className="form-control btn btn-green1 text-light btn-outline input-width"
                              id="exampleInputPassword1"
                            >
                              Register Now
                            </button>
                          </div>
                          {this.state.error ? (
                            <div className="alert alert-danger" role="alert">
                              {this.state.error}
                            </div>
                          ) : (
                            ""
                          )}

                          {this.state.isRegistered ? (
                            <div className="alert alert-success" role="alert">
                              {"Registration success!"}
                            </div>
                          ) : (
                            ""
                          )}
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </nav>
        </div>
      </React.Fragment>
    );
  }
}
const StyledBadge = withStyles(theme => ({
  badge: {
    border: `2px solid ${theme.palette.background.paper}`,
    padding: '0 4px',
  },
}))(Badge);
const mapStateToProps = state => {
  const { isLoggedIn, isRegistered, email, fullname, id } = state;
  const test = state.test;
  return { isLoggedIn, test, isRegistered, email, fullname, id };
};
const mapDispatchToProps = dispatch => {
  return {
    userLogin: userdata => dispatch(userLogin(userdata)),
    userRegister: () => dispatch(userRegister()),
    userLogout: () => dispatch(userLogout()),
    userTest: () => dispatch(userTest())
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Nav);
