const initialState = {
  isLoggedIn: false,
  isRegistered: false,
  fullname: "",
  email: "",
  id: "",
  test: "vandan",
  kitchens:'',
  cart:''
};
const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case "USER_LOGIN":
      return {
        ...state,
        isLoggedIn: true,
        fullname: action.payload.fullname,
        email: action.payload.email,
        id: action.payload.id
      };
    case "USER_REGISTER":
      return {
        ...state,
        isRegistered: true
      };
    case "USER_LOGOUT":
      return {
        ...state,
        isLoggedIn: false
      };
      case "FETCH_KITCHENS":
      return {
        ...state,
        kitchens: action.payload.kitchens
      };
      case "FETCH_CART":
      return {
        ...state,
        cart: action.payload.cartitems
      };
      case "UPDATE_CART":
      return {
        ...state,
        cart: action.payload.cart
      };
    case "USER_TEST":
      return {
        ...state,
        test: state.state.test + "Khamkar"
      };
      
    default:
      return state;
  }
};
export default userReducer;
