const initialState = {
  isLoggedIn: false,
  isRegistered: false,
  test: "vandan"
};
const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case "USER_LOGIN":
      return {
        ...state,
        isLoggedIn: true,
        userdata: action.payload.userdata
      };
    case "USER_REGISTER":
      return {
        ...state,
        isRegistered: true
      };
    case "USER_LOGOUT":
      return {
        ...state,
        isLoggedIn: false
      };
    case "USER_TEST":
      return {
        ...state,
        test: state.state.test + "Khamkar"
      };
    default:
      return state;
  }
};
export default userReducer;
