import React, { Component } from "react";
import { connect } from "react-redux";
import axios from "axios";
import status1 from "../img/status1.jpg";
class DisplayItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      providerkitchen: ""
    };
  }
  componentDidMount() {
    axios
      .get(
        "http://localhost:3001/user/getuniquekitchen/" +
          this.props.match.params.id,
        {
          headers: {
            auth_token: localStorage.getItem("auth_token")
          }
        }
      )
      .then(data => {
        console.log(data.data);
        this.setState({ providerkitchen: data.data });
      })
      .catch(err => {
        if (err) console.log(err);
      });
  }
  render() {
    return (
      <div>
        <div className="container">
          <div className="row mt-2">
            <div class="jumbotron">
              <h1 class="display-4">
                {this.state.providerkitchen.kitchen_name}
              </h1>
              <p class="lead">
                {this.state.providerkitchen.kitchen_description}
              </p>
              <hr class="my-4" />
              <p>
                It uses utility classes for typography and spacing to space
                content out within the larger container.
              </p>
              <p class="lead">
                <a
                  class="btn btn-success text-light btn-sm"
                  href="#"
                  role="button"
                >
                  Bookmark
                </a>
                <a
                  class="btn btn-success text-light btn-sm ml-1"
                  href="#"
                  role="button"
                >
                  Add a Review
                </a>
                <a
                  class="btn btn-success text-light btn-sm ml-1"
                  href="#"
                  role="button"
                >
                  Rate
                </a>
              </p>
              <p>
                <div class="row">
                  <div class="col-md-6">
                    <input type="text" class="form-control" placeholder='Search food by name'></input>
                  </div>
                  <p>
                <i class="fas fa-search"></i> SEARCH
              </p>
                </div>
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = state => {
  const { isLoggedIn, kitchens } = state;

  return { isLoggedIn, kitchens };
};
export default connect(mapStateToProps)(DisplayItems);
