const initialState = {
  isLoggedIn: false,
  userdata: ""
};
const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case "USER_LOGIN":
      return {
        ...state,
        isLoggedIn: true,
        userdata: action.payload.userdata
      };
    case "USER_REGISTER":
      return {
        ...state,
        userdata: action.payload.userdata
      };
    case "USER_LOGOUT":
      return {
        ...state,
        isLoggedIn: false
      };
      case "USER_TEST":
      return {
        ...state,
        isLoggedIn: false
      };
    default:
      return {
        state
      };
  }
};
export default userReducer;
